import React from "react";
import ProTable from "antd";

const MainLayout = () => {
    const columns = [
        {
            title: "ID",
            dataIndex: "id",
            key: "id",
            valueType: "text",
        },
        {
            title: "Tên",
            dataIndex: "name",
            key: "name",
            valueType: "text",
            search: true, // Bật tìm kiếm cho cột này
        },
        {
            title: "Tuổi",
            dataIndex: "age",
            key: "age",
            valueType: "digit", // Kiểu số
        },
        {
            title: "Địa chỉ",
            dataIndex: "address",
            key: "address",
        },
        {
            title: "Thao tác",
            key: "action",
            valueType: "option",
            render: (text, record) => [
                <a key="edit" onClick={() => console.log("Sửa", record)}>Sửa</a>,
                <a key="delete" onClick={() => console.log("Xóa", record)}>Xóa</a>,
            ],
        },
    ];

    return (
        <ProTable
            columns={columns}
            request={async (params) => {
                // Giả lập dữ liệu API
                console.log("Params request:", params); // Xem các tham số gửi lên server
                return {
                    data: [
                        { id: 1, name: "Nguyen Van A", age: 25, address: "Hanoi" },
                        { id: 2, name: "Tran Van B", age: 30, address: "HCM" },
                    ],
                    success: true,
                    total: 2,
                };
            }}
            rowKey="id" // Khóa duy nhất của mỗi hàng
            search={{
                labelWidth: "auto", // Tự động căn chỉnh nhãn
            }}
            pagination={{
                pageSize: 5, // Số dòng trên mỗi trang
            }}
            dateFormatter="string" // Định dạng ngày tháng nếu có
        />
    );
};

export default MainLayout;
