import React, { useState } from "react";
import { ProTable } from "@ant-design/pro-table";
import axios from "axios";
import parsePaginationLinks from "../../utils/paginationUtils"; // Import hàm mặc định

const Users = () => {
    const [paginationLinks, setPaginationLinks] = useState({}); // Lưu các link pagination
    const [pageSize, setPageSize] = useState(20); // Lưu trạng thái pageSize do người dùng chọn
    const [currentRow, setCurrentRow] = useState(null); // Lưu bản ghi hiện tại để xử lý modal

    const handleUpdateModalOpen = (isOpen) => {
        console.log(`Update Modal Open: ${isOpen}`);
    };

    const columns = [
        {
            title: "ID",
            dataIndex: "id",
            key: "id",
            valueType: "text",
        },
        {
            title: "Name",
            dataIndex: "name",
            key: "name",
            valueType: "text",
        },
        {
            title: "Price",
            dataIndex: "price",
            key: "price",
            valueType: "money",
        },
        {
            title: "Amount",
            dataIndex: "amount",
            key: "amount",
            valueType: "digit",
        },
        {
            title: "Option",
            dataIndex: "option",
            key: "option",
            valueType: "option",
            render: (_, record) => [
                <a
                    key="config"
                    onClick={() => {
                        handleUpdateModalOpen(true);
                        setCurrentRow(record);
                    }}
                >
                    Configuration
                </a>,
                <a
                    key="subscribeAlert"
                    style={{ marginLeft: 8 }}
                    onClick={() => {
                        console.log("Subscribe to alerts clicked for:", record);
                    }}
                >
                    Subscribe to alerts
                </a>,
            ],
        },
    ];

    return (
        <ProTable
            columns={columns}
            request={async (params) => {
                try {
                    const response = await axios.get("http://localhost:8080/api/products", {
                        params: { page: params.current - 1, size: params.pageSize },
                        headers: {
                            "Content-Type": "application/json",
                            Authorization: "Bearer eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJhZG1pbiIsImV4cCI6MTczMzkxMDkwMywiYXV0aCI6IlJPTEVfQURNSU4gUk9MRV9VU0VSIiwiaWF0IjoxNzMzODI0NTAzfQ.1RXQD4j5UI33MyfsEIICh9HI3XhKZi93YoQrgBltY0ylPGSXcBuMJ33CgajPa7VL6kyoUtknoQnAhZZ0MU6DKA",
                        },
                    });

                    // Parse Link Header để lấy các link pagination
                    const links = parsePaginationLinks(response.headers.link);
                    setPaginationLinks(links); // Lưu các link vào state

                    return {
                        data: response.data, // Dữ liệu từ API
                        total: parseInt(response.headers["x-total-count"], 10), // Tổng số bản ghi
                        success: true, // Đánh dấu thành công
                    };
                } catch (error) {
                    console.error("Error fetching data:", error);
                    return {
                        data: [],
                        total: 0,
                        success: false,
                    };
                }
            }}
            rowKey="id" // Khóa duy nhất
            pagination={{
                pageSize: pageSize, // Lấy giá trị pageSize từ state
                showSizeChanger: true,
                showQuickJumper: true,
                onShowSizeChange: (_, size) => {
                    setPageSize(size); // Cập nhật state pageSize khi người dùng thay đổi
                },
            }}
            search={{
                labelWidth: "auto",
            }}
            dateFormatter="string"
            headerTitle="Product List"
        />
    );
};

export default Users;
