import logo from './logo.svg';
import routes from './router';
import { Route, Routes, BrowserRouter } from 'react-router-dom';
import NotFound from './pages/404';
import Home from './pages/home';
import Users from './pages/users';
import {useEffect, useState} from 'react';
import MainLayout from './pages/layouts';
import Login from "./pages/login";
import apiService from "./services/api";

function App() {
  const [isAuth, setIsAuth] = useState(false); // TODO: Update authentication

  useEffect(() => {
    const token = localStorage.getItem("token");
    if(token) {
      apiService.get("account").then((res) => {
        if(res.activated) {
          setIsAuth(true);
        }
      });
    }
  }, []);

  if(!isAuth) {
    return <Login />;
  }

  return ( <div>
    <Routes>
      <Route path='/' element={isAuth ? <MainLayout/> : <p>Login</p>}>
        {
          routes.map(route => <Route path={route.path} key={route.path} element={route.component} />)
        }
        <Route path='*' element={NotFound}/>
      </Route>
    </Routes>
    </div>
  );
}

export default App;
