import React, { useRef, useState } from "react";
import { ProTable } from "@ant-design/pro-table";
import { Slider } from "antd";
import axios from "axios";

const Users = () => {
    const [pageSize, setPageSize] = useState(20);
    const formRef = useRef();

    const buildJHipsterFilters = (params) => {
        const filters = {};

        if (params.name) {
            filters["name.contains"] = params.name;
        }

        const priceRange = params.priceRange || [0, 50000000];
        filters["price.greaterThanOrEqual"] = priceRange[0];
        filters["price.lessThanOrEqual"] = priceRange[1];

        const amountRange = params.amountRange || [0, 10000];
        filters["amount.greaterThanOrEqual"] = amountRange[0];
        filters["amount.lessThanOrEqual"] = amountRange[1];

        return filters;
    };

    const columns = [
        {
            title: "ID",
            dataIndex: "id",
            key: "id",
            render: (text) => (
                <a href={`/products/${text}`} target="_blank" rel="noopener noreferrer">
                    {text}
                </a>
            ),
        },
        {
            title: "Name",
            dataIndex: "name",
            key: "name",
        },
        {
            title: "Price",
            dataIndex: "price",
            key: "price",
            renderFormItem: (_, { type }) => {
                if (type === "form") {
                    return null;
                }

                return (
                    <Slider
                        range
                        min={0}
                        max={50000000}
                        step={1000}
                        tooltip={{
                            formatter: (value) => `$${value.toLocaleString()}`,
                        }}
                        onAfterChange={(value) => {
                            if (formRef.current) {
                                formRef.current.setFieldsValue({
                                    priceRange: value,
                                });
                            }
                        }}
                    />
                );
            },
        },
        {
            title: "Amount",
            dataIndex: "amount",
            key: "amount",
            renderFormItem: (_, { type }) => {
                if (type === "form") {
                    return null;
                }

                return (
                    <Slider
                        range
                        min={0}
                        max={10000}
                        step={100}
                        tooltip={{
                            formatter: (value) => `${value}`,
                        }}
                        onAfterChange={(value) => {
                            if (formRef.current) {
                                formRef.current.setFieldsValue({
                                    amountRange: value,
                                });
                            }
                        }}
                    />
                );
            },
        },
    ];

    return (
        <ProTable
            columns={columns}
            request={async (params) => {
                const filters = buildJHipsterFilters(params);

                const response = await axios.get("http://localhost:8080/api/products", {
                    params: {
                        page: params.current - 1,
                        size: params.pageSize,
                        ...filters,
                    },
                });

                return {
                    data: response.data,
                    total: parseInt(response.headers["x-total-count"], 10),
                    success: true,
                };
            }}
            rowKey="id"
            pagination={{
                pageSize: pageSize,
                showSizeChanger: true,
            }}
            manualRequest={true}
            form={{
                syncToUrl: false,
                formRef: formRef,
                initialValues: {
                    priceRange: [0, 50000000],
                    amountRange: [0, 10000],
                },
            }}
            search={{
                labelWidth: "auto",
                optionRender: ({ searchText, resetText }, { form }) => (
                    <>
                        <button
                            type="button"
                            onClick={() => {
                                form.submit();
                            }}
                        >
                            {searchText}
                        </button>
                        <button
                            type="button"
                            onClick={() => {
                                form.resetFields();
                            }}
                        >
                            {resetText}
                        </button>
                    </>
                ),
            }}
            headerTitle="Product List"
        />
    );
};

export default Users;
