import React from "react";
import { ProTable } from "@ant-design/pro-table";
import axios from "axios";

const Users = () => {
    const columns = [
        {
            title: "ID",
            dataIndex: "id",
            key: "id",
            valueType: "text",
        },
        {
            title: "Name",
            dataIndex: "name",
            key: "name",
            valueType: "text",
        },
        {
            title: "Price",
            dataIndex: "price",
            key: "price",
            valueType: "money",
        },
        {
            title: "Amount",
            dataIndex: "amount",
            key: "amount",
            valueType: "digit",
        },
    ];

    return (
        <ProTable
            columns={columns}
            request={async (params) => {
                try {
                    const response = await axios.get("http://localhost:8080/api/products", {
                        params: { page: params.current - 1, size: params.pageSize },
                        headers: {
                            "Content-Type": "application/json",
                            Authorization: "Bearer YOUR_ACCESS_TOKEN",
                        },
                    });

                    return {
                        data: response.data, // Dữ liệu từ API
                        total: parseInt(response.headers["x-total-count"], 10), // Tổng số bản ghi từ header
                        success: true,
                    };
                } catch (error) {
                    console.error("Error fetching data:", error);
                    return {
                        data: [],
                        total: 0,
                        success: false,
                    };
                }
            }}
            rowKey="id" // Dùng `id` làm khóa duy nhất
            pagination={{
                pageSize: 20, // Số bản ghi mỗi trang
                showSizeChanger: true, // Cho phép thay đổi số bản ghi mỗi trang
                showQuickJumper: true, // Cho phép nhảy tới trang bất kỳ
            }}
            search={{
                labelWidth: "auto", // Tự động căn chỉnh nhãn
            }}
            dateFormatter="string"
        />
    );
};

export default Users;
