import React, { useRef, useState } from "react";
import { ProTable } from "@ant-design/pro-table";
import { Slider } from "antd";
import axios from "axios";

const Users = () => {
    const [pageSize, setPageSize] = useState(20);
    const formRef = useRef(); // Tham chiếu form

    // Hàm xây dựng bộ lọc API
    const buildJHipsterFilters = (params) => {
        const filters = {};

        if (params.name) {
            filters["name.contains"] = params.name;
        }

        if (params.priceRange?.length === 2) {
            filters["price.greaterThanOrEqual"] = params.priceRange[0];
            filters["price.lessThanOrEqual"] = params.priceRange[1];
        }

        if (params.amountRange?.length === 2) {
            filters["amount.greaterThanOrEqual"] = params.amountRange[0];
            filters["amount.lessThanOrEqual"] = params.amountRange[1];
        }

        return filters;
    };

    // Định nghĩa các cột của bảng
    const columns = [
        {
            title: "ID",
            dataIndex: "id",
            key: "id",
            valueType: "text",
            render: (text) => (
                <a href={`/products/${text}`} target="_blank" rel="noopener noreferrer">
                    {text}
                </a>
            ),
        },
        {
            title: "Name",
            dataIndex: "name",
            key: "name",
            valueType: "text",
        },
        {
            title: "Price",
            dataIndex: "price",
            key: "price",
            valueType: "money",
            renderFormItem: (_, { type }) => {
                if (type === "form") {
                    return null;
                }

                return (
                    <Slider
                        range
                        min={0}
                        max={50000000}
                        step={1000}
                        tooltip={{
                            formatter: (value) => `$${value.toLocaleString()}`, // Hiển thị $
                        }}
                        onAfterChange={(value) => {
                            // Gán giá trị vào form và submit
                            if (formRef.current) {
                                formRef.current.setFieldsValue({
                                    priceRange: value,
                                });
                                formRef.current.submit(); // Submit lại form để gửi API
                            }
                        }}
                    />
                );
            },
        },
        {
            title: "Amount",
            dataIndex: "amount",
            key: "amount",
            valueType: "digit",
            renderFormItem: (_, { type }) => {
                if (type === "form") {
                    return null;
                }

                return (
                    <Slider
                        range
                        min={0}
                        max={10000}
                        step={100}
                        tooltip={{
                            formatter: (value) => `${value}`,
                        }}
                        onAfterChange={(value) => {
                            // Gán giá trị vào form và submit
                            if (formRef.current) {
                                formRef.current.setFieldsValue({
                                    amountRange: value,
                                });
                                formRef.current.submit(); // Submit lại form để gửi API
                            }
                        }}
                    />
                );
            },
        },
    ];

    return (
        <ProTable
            columns={columns}
            request={async (params) => {
                try {
                    // Xây dựng bộ lọc
                    const filters = buildJHipsterFilters(params);

                    // Gửi request API
                    const response = await axios.get("http://localhost:8080/api/products", {
                        params: {
                            page: params.current - 1,
                            size: params.pageSize,
                            ...filters,
                        },
                        headers: {
                            "Content-Type": "application/json",
                            Authorization: "Bearer eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJhZG1pbiIsImV4cCI6MTczMzkxMDkwMywiYXV0aCI6IlJPTEVfQURNSU4gUk9MRV9VU0VSIiwiaWF0IjoxNzMzODI0NTAzfQ.1RXQD4j5UI33MyfsEIICh9HI3XhKZi93YoQrgBltY0ylPGSXcBuMJ33CgajPa7VL6kyoUtknoQnAhZZ0MU6DKA", // Thay bằng token thật của bạn
                        },
                    });

                    return {
                        data: response.data,
                        total: parseInt(response.headers["x-total-count"], 10),
                        success: true,
                    };
                } catch (error) {
                    console.error("Error fetching data:", error);
                    return {
                        data: [],
                        total: 0,
                        success: false,
                    };
                }
            }}
            rowKey="id"
            pagination={{
                pageSize: pageSize,
                showSizeChanger: true,
                showQuickJumper: true,
                onShowSizeChange: (_, size) => {
                    setPageSize(size);
                },
            }}
            form={{
                syncToUrl: false,
                formRef: formRef, // Tham chiếu form
                initialValues: {
                    priceRange: [0, 50000000],
                    amountRange: [0, 10000],
                },
            }}
            search={{
                labelWidth: "auto",
            }}
            dateFormatter="string"
            headerTitle="Product List"
        />
    );
};

export default Users;
