import React, { useState } from "react";
import { ProTable } from "@ant-design/pro-table";
import { Slider } from "antd";
import axios from "axios";

const Users = () => {
    const [pageSize, setPageSize] = useState(20);

    // Hàm xây dựng bộ lọc API
    const buildJHipsterFilters = (params) => {
        const filters = {};

        if (params.name) {
            filters["name.contains"] = params.name;
        }

        if (params.priceRange) {
            filters["price.greaterThanOrEqual"] = params.priceRange[0];
            filters["price.lessThanOrEqual"] = params.priceRange[1];
        }

        if (params.amountRange) {
            filters["amount.greaterThanOrEqual"] = params.amountRange[0];
            filters["amount.lessThanOrEqual"] = params.amountRange[1];
        }

        return filters;
    };

    // Định nghĩa các cột của bảng
    const columns = [
        {
            title: "ID",
            dataIndex: "id",
            key: "id",
            valueType: "text",
        },
        {
            title: "Name",
            dataIndex: "name",
            key: "name",
            valueType: "text",
        },
        {
            title: "Price",
            dataIndex: "price",
            key: "price",
            valueType: "money",
            renderFormItem: (_, { type, form }) => {
                if (type === "form") {
                    return null; // Không hiển thị trong form thêm/sửa
                }

                return (
                    <Slider
                        range // Chế độ chọn khoảng giá trị
                        min={0} // Giá trị tối thiểu
                        max={50000000} // Giá trị tối đa
                        step={1000} // Bước nhảy
                        tooltip={{ formatter: (value) => `${value.toLocaleString()}₫` }} // Hiển thị tooltip
                        onChange={(value) => {
                            form.setFieldsValue({
                                priceRange: value, // Gán giá trị khoảng vào form
                            });
                        }}
                    />
                );
            },
        },
        {
            title: "Amount",
            dataIndex: "amount",
            key: "amount",
            valueType: "digit",
            renderFormItem: (_, { type, form }) => {
                if (type === "form") {
                    return null; // Không hiển thị trong form thêm/sửa
                }

                return (
                    <Slider
                        range
                        min={0}
                        max={10000}
                        step={100}
                        tooltip={{ formatter: (value) => `${value}` }}
                        onChange={(value) => {
                            form.setFieldsValue({
                                amountRange: value, // Gán giá trị khoảng vào form
                            });
                        }}
                    />
                );
            },
        },
    ];

    return (
        <ProTable
            columns={columns}
            request={async (params) => {
                try {
                    // Xây dựng các bộ lọc
                    const filters = buildJHipsterFilters(params);

                    // Gửi request API
                    const response = await axios.get("http://localhost:8080/api/products", {
                        params: {
                            page: params.current - 1,
                            size: params.pageSize,
                            ...filters,
                        },
                        headers: {
                            "Content-Type": "application/json",
                            Authorization: "Bearer eyJhbGciOiJIUzUxMiJ9.eyJzdWIiOiJhZG1pbiIsImV4cCI6MTczMzkxMDkwMywiYXV0aCI6IlJPTEVfQURNSU4gUk9MRV9VU0VSIiwiaWF0IjoxNzMzODI0NTAzfQ.1RXQD4j5UI33MyfsEIICh9HI3XhKZi93YoQrgBltY0ylPGSXcBuMJ33CgajPa7VL6kyoUtknoQnAhZZ0MU6DKA", // Thay bằng token thật của bạn
                        },
                    });

                    return {
                        data: response.data,
                        total: parseInt(response.headers["x-total-count"], 10),
                        success: true,
                    };
                } catch (error) {
                    console.error("Error fetching data:", error);
                    return {
                        data: [],
                        total: 0,
                        success: false,
                    };
                }
            }}
            rowKey="id"
            pagination={{
                pageSize: pageSize,
                showSizeChanger: true,
                showQuickJumper: true,
                onShowSizeChange: (_, size) => {
                    setPageSize(size);
                },
            }}
            form={{
                syncToUrl: false, // Không đồng bộ form với URL
                initialValues: {
                    priceRange: [0, 50000000], // Giá trị mặc định cho Price
                    amountRange: [0, 10000], // Giá trị mặc định cho Amount
                },
            }}
            search={{
                labelWidth: "auto",
            }}
            dateFormatter="string"
            headerTitle="Product List"
        />
    );
};

export default Users;
