import React, { useState } from "react";
import { ProTable } from "@ant-design/pro-table";
import { Slider } from "antd";
import axios from "axios";

const Users = () => {
    const [pageSize, setPageSize] = useState(20);

    // Hàm tạo các bộ lọc cho API JHipster
    const buildJHipsterFilters = (params) => {
        const filters = {};

        if (params.name) {
            filters["name.contains"] = params.name;
        }

        if (params.priceMin !== undefined) {
            filters["price.greaterThanOrEqual"] = params.priceMin;
        }

        if (params.priceMax !== undefined) {
            filters["price.lessThanOrEqual"] = params.priceMax;
        }

        if (params.amountMin !== undefined) {
            filters["amount.greaterThanOrEqual"] = params.amountMin;
        }

        if (params.amountMax !== undefined) {
            filters["amount.lessThanOrEqual"] = params.amountMax;
        }

        return filters;
    };

    const columns = [
        {
            title: "ID",
            dataIndex: "id",
            key: "id",
            valueType: "text",
        },
        {
            title: "Name",
            dataIndex: "name",
            key: "name",
            valueType: "text",
        },
        {
            title: "Price",
            dataIndex: "price",
            key: "price",
            valueType: "money",
            renderFormItem: (_, { type, form }) => {
                if (type === "form") {
                    return null; // Không hiển thị trong form thêm/sửa
                }

                // Kiểm tra nếu form không tồn tại
                if (!form) {
                    console.warn("Form is undefined!");
                    return null;
                }

                return (
                    <Slider
                        range
                        min={0}
                        max={50000000}
                        step={1000}
                        tooltip={{ formatter: (value) => `${value.toLocaleString()}₫` }}
                        onChange={(value) => {
                            // Kiểm tra form trước khi gọi setFieldsValue
                            if (form) {
                                form.setFieldsValue({
                                    priceMin: value[0],
                                    priceMax: value[1],
                                });
                            }
                        }}
                    />
                );
            },
        },
        {
            title: "Amount",
            dataIndex: "amount",
            key: "amount",
            valueType: "digit",
            renderFormItem: (_, { type, form }) => {
                if (type === "form") {
                    return null; // Không hiển thị trong form thêm/sửa
                }

                // Kiểm tra nếu form không tồn tại
                if (!form) {
                    console.warn("Form is undefined!");
                    return null;
                }

                return (
                    <Slider
                        range
                        min={0}
                        max={10000}
                        step={100}
                        tooltip={{ formatter: (value) => `${value}` }}
                        onChange={(value) => {
                            // Kiểm tra form trước khi gọi setFieldsValue
                            if (form) {
                                form.setFieldsValue({
                                    amountMin: value[0],
                                    amountMax: value[1],
                                });
                            }
                        }}
                    />
                );
            },
        },
    ];

    return (
        <ProTable
            columns={columns}
            request={async (params) => {
                try {
                    // Xây dựng các bộ lọc
                    const filters = buildJHipsterFilters(params);

                    // Gửi request API
                    const response = await axios.get("http://localhost:8080/api/products", {
                        params: {
                            page: params.current - 1,
                            size: params.pageSize,
                            ...filters,
                        },
                        headers: {
                            "Content-Type": "application/json",
                            Authorization: "Bearer <your-token>", // Thay bằng token thật của bạn
                        },
                    });

                    return {
                        data: response.data,
                        total: parseInt(response.headers["x-total-count"], 10),
                        success: true,
                    };
                } catch (error) {
                    console.error("Error fetching data:", error);
                    return {
                        data: [],
                        total: 0,
                        success: false,
                    };
                }
            }}
            rowKey="id"
            pagination={{
                pageSize: pageSize,
                showSizeChanger: true,
                showQuickJumper: true,
                onShowSizeChange: (_, size) => {
                    setPageSize(size);
                },
            }}
            form={{
                syncToUrl: false, // Không đồng bộ form với URL
                initialValues: {
                    priceMin: 0,
                    priceMax: 50000000,
                    amountMin: 0,
                    amountMax: 10000,
                }, // Giá trị mặc định cho form
            }}
            search={{
                labelWidth: "auto",
            }}
            dateFormatter="string"
            headerTitle="Product List"
        />
    );
};

export default Users;
