import React, { useState } from "react";
import { ProTable } from "@ant-design/pro-table";
import { Slider } from "antd";

const Users = () => {
    const [pageSize, setPageSize] = useState(20);

    // Hàm xây dựng bộ lọc API
    const buildJHipsterFilters = (params) => {
        const filters = {};

        if (params.name) {
            filters["name.contains"] = params.name;
        }

        if (params.priceRange) {
            filters["price.greaterThanOrEqual"] = params.priceRange[0];
            filters["price.lessThanOrEqual"] = params.priceRange[1];
        }

        if (params.amountRange) {
            filters["amount.greaterThanOrEqual"] = params.amountRange[0];
            filters["amount.lessThanOrEqual"] = params.amountRange[1];
        }

        return filters;
    };

    const columns = [
        {
            title: "ID",
            dataIndex: "id",
            key: "id",
            valueType: "text",
        },
        {
            title: "Name",
            dataIndex: "name",
            key: "name",
            valueType: "text",
        },
        {
            title: "Price",
            dataIndex: "price",
            key: "price",
            valueType: "money",
            renderFormItem: (_, { form }) => (
                <Slider
                    range
                    min={0}
                    max={50000000}
                    step={1000}
                    tooltip={{ formatter: (value) => `${value.toLocaleString()}₫` }}
                    onChange={(value) => {
                        form.setFieldsValue({
                            priceRange: value,
                        });
                    }}
                />
            ),
        },
        {
            title: "Amount",
            dataIndex: "amount",
            key: "amount",
            valueType: "digit",
            renderFormItem: (_, { form }) => (
                <Slider
                    range
                    min={0}
                    max={10000}
                    step={100}
                    tooltip={{ formatter: (value) => `${value}` }}
                    onChange={(value) => {
                        form.setFieldsValue({
                            amountRange: value,
                        });
                    }}
                />
            ),
        },
    ];

    return (
        <ProTable
            columns={columns}
            request={async (params) => {
                // Chỉ in log để kiểm tra params
                console.log("Request Params:", params);

                // Giả lập trả dữ liệu mẫu (bỏ qua phần API thực tế để tập trung vào filter)
                const sampleData = [
                    { id: 1, name: "Product 1", price: 200000, amount: 500 },
                    { id: 2, name: "Product 2", price: 5000000, amount: 2000 },
                    { id: 3, name: "Product 3", price: 10000000, amount: 8000 },
                ];

                return {
                    data: sampleData.filter((item) => {
                        const priceFilter = params.priceRange
                            ? item.price >= params.priceRange[0] &&
                            item.price <= params.priceRange[1]
                            : true;
                        const amountFilter = params.amountRange
                            ? item.amount >= params.amountRange[0] &&
                            item.amount <= params.amountRange[1]
                            : true;
                        return priceFilter && amountFilter;
                    }),
                    total: 3,
                    success: true,
                };
            }}
            rowKey="id"
            pagination={{
                pageSize: pageSize,
                showSizeChanger: true,
                showQuickJumper: true,
                onShowSizeChange: (_, size) => {
                    setPageSize(size);
                },
            }}
            form={{
                syncToUrl: false,
                initialValues: {
                    priceRange: [0, 50000000],
                    amountRange: [0, 10000],
                },
            }}
            search={{
                labelWidth: "auto",
            }}
            dateFormatter="string"
            headerTitle="Product List"
        />
    );
};

export default Users;
