import React, { useState } from "react";
import { ProTable } from "@ant-design/pro-table";
import { DatePicker, InputNumber, Row, Col } from "antd";
import axios from "axios";
import parsePaginationLinks from "../../utils/paginationUtils"; // Import hàm mặc định
import { FormattedMessage } from "@umijs/max";
const { RangePicker } = DatePicker;

const Users = () => {
    const [paginationLinks, setPaginationLinks] = useState({});
    const [pageSize, setPageSize] = useState(20);

    // Hàm để xây dựng bộ lọc cho JHipster
    const buildJHipsterFilters = (params) => {
        const filters = {};

        // Lọc theo name nếu có
        if (params.name) {
            filters["name.contains"] = params.name;
        }

        // Lọc theo amount (greaterThan và lessThan)
        if (params.amountRange && params.amountRange.length === 2) {
            if (params.amountRange[0]) {
                filters["amount.greaterThanOrEqual"] = params.amountRange[0];
            }
            if (params.amountRange[1]) {
                filters["amount.lessThanOrEqual"] = params.amountRange[1];
            }
        }

        return filters;
    };

    const columns = [
        {
            title: "ID",
            dataIndex: "id",
            key: "id",
            valueType: "text",
        },
        {
            title: "Name",
            dataIndex: "name",
            key: "name",
            valueType: "text",
        },
        {
            title: "Price",
            dataIndex: "price",
            key: "price",
            valueType: "money",
        },
        {
            title: "Amount",
            dataIndex: "amount",
            key: "amount",
            valueType: "digit",
        },
    ];

    return (
        <ProTable
            columns={columns}
            request={async (params) => {
                try {
                    // Xây dựng bộ lọc cho JHipster
                    const filters = buildJHipsterFilters(params);

                    const response = await axios.get("http://localhost:8080/api/products", {
                        params: {
                            page: params.current - 1,
                            size: params.pageSize,
                            ...filters,
                        },
                        headers: {
                            "Content-Type": "application/json",
                            Authorization: "Bearer <your-token>", // Thay thế bằng token thật của bạn
                        },
                    });

                    return {
                        data: response.data,
                        total: parseInt(response.headers["x-total-count"], 10),
                        success: true,
                    };
                } catch (error) {
                    console.error("Error fetching data:", error);
                    return {
                        data: [],
                        total: 0,
                        success: false,
                    };
                }
            }}
            rowKey="id"
            pagination={{
                pageSize: pageSize,
                showSizeChanger: true,
                showQuickJumper: true,
                onShowSizeChange: (_, size) => {
                    setPageSize(size);
                },
            }}
            search={{
                labelWidth: "auto",
                // Tạo trường lọc Amount range
                filterType: "query",
                optionRender: (searchConfig, { form }) => {
                    return (
                        <Row gutter={8}>
                            <Col span={8}>
                                <InputNumber
                                    placeholder="Min Amount"
                                    onChange={(value) => form.setFieldsValue({ amountRange: [value, form.getFieldValue('amountRange')[1]] })}
                                />
                            </Col>
                            <Col span={8}>
                                <InputNumber
                                    placeholder="Max Amount"
                                    onChange={(value) => form.setFieldsValue({ amountRange: [form.getFieldValue('amountRange')[0], value] })}
                                />
                            </Col>
                        </Row>
                    );
                },
            }}
            dateFormatter="string"
            headerTitle="Product List"
        />
    );
};

export default Users;
